query:
query ($id: ID!) {
  posts(
    where: {state: {equals: "published"}, id: {equals: $id}, style: {in: ["news", "frame", "blank", "report", "project3", "embedded", "scrollablevideo"]}}
  ) {
    id
    slug
    style
    title: name
    heroImage {
      resized {
        original
        w480
        w800
        w1200
        w1600
        w2400
      }
    }
    ogImage {
      resized {
        original
        w480
        w800
        w1200
        w1600
        w2400
      }
    }
    publishTime
    readingTime
    state
    content
    summary
    actionList
    citation
    heroCaption
    ogDescription
    categories {
      id
      title
      slug
    }
    manualOrderOfWriters
    manualOrderOfPhotographers
    manualOrderOfCameraOperators
    manualOrderOfDesigners
    manualOrderOfEngineers
    manualOrderOfDataAnalysts
    writers {
      id
      name
    }
    photographers {
      id
      name
    }
    cameraOperators {
      id
      name
    }
    designers {
      id
      name
    }
    engineers {
      id
      name
    }
    dataAnalysts {
      id
      name
    }
    leadingEmbeddedCode
    otherByline
    tags(where: {state: {equals: "active"}}) {
      id
      name
    }
    relatedPosts(
      where: {state: {equals: "published"}, style: {in: ["news", "frame", "blank", "report", "project3", "embedded", "scrollablevideo"]}}
      orderBy: {publishTime: desc}
    ) {
      id
      slug
      style
      title: name
      heroImage {
        resized {
          original
          w480
          w800
          w1200
          w1600
          w2400
        }
      }
      ogImage {
        resized {
          original
          w480
          w800
          w1200
          w1600
          w2400
        }
      }
      publishTime
      readingTime
    }
  }
}
